import React,{useState} from 'react'

const Form = () => {
    const [screen, setScreen] = useState(1);
    const [userName, setUserName] = useState('');
    const [email, setEmail] = useState('');
  
    const handleBookClick = () => {
      setScreen(2);
    };
  
    const handleConfirmBooking = (event) => {
      event.preventDefault();
      setScreen(1);
    };
  
    return (
      <div className="App">
        {screen === 1 && (
          <div className="screen" id="screen1">
            <div id="movieDetails">
              <h2>Movie Title</h2>
              <p>Some movie details here</p>
              <img src="movie-image.jpg" alt="Movie Poster" width="200" />
              <br />
              <button onClick={handleBookClick}>Book Now</button>
            </div>
          </div>
        )}
  
        {screen === 2 && (
          <div className="screen" id="screen2">
            <div id="bookingForm">
              <h2>Booking Form</h2>
              <form onSubmit={handleConfirmBooking}>
                <label htmlFor="movieName">Movie Name:</label>
                <input type="text" id="movieName" value="Movie Title" readOnly />
                <br />
                <label htmlFor="userName">Your Name:</label>
                <input
                  type="text"
                  id="userName"
                  value={userName}
                  onChange={(e) => setUserName(e.target.value)}
                  required
                />
                <br />
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  required
                />
                <br />
                <button type="submit">Confirm Booking</button>
              </form>
            </div>
          </div>
        )}
      </div>
    );
  }

export default Form
