import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import { useSelector } from "react-redux"

const Summary = () => {
  const { showId } = useParams();
  console.log(showId);

  const list = useSelector(store => store.user.nowPlayingMovies);
  console.log(list);

  //  const filterList = list.filter((res) => res.show.id === 34653);
  const filterList = list.filter((res) => res.show.id == showId);

  console.log(filterList)

  return (
    <div className='flex mx-[200px]'>
      <div className=' mt-10 border border-blue-600 h-[500px] w-[700px]'>
        <h1 className='font-[700] text-[50px] flex justify-center items-center'> {filterList[0].show.name}</h1>
        <div className='text-[20px] font-[600] mx-8'>Summary</div>
        <div className='w-[650px] mx-8'> {filterList[0].show.summary}</div>

<div className='flwx'>
        <span className='flex text-[20px] font-[600] mx-8'>Language : 
      </span> <span className=''>{filterList[0].show.language}</span>
      </div>     


      </div>


      <div className='w-[300px] border border-r-emerald-100 mt-10'>
        <div> {filterList[0].show.image === null ?
          <div>No image </div> : <img alt='nothing'
            className='w-[300px] h-[450px]'
            src={filterList[0].show.image.medium}

          />}
          <div className='flex justify-center items-center font-[600] text-[25px] mt-[5px]'>

            <a href={filterList[0].show.officialSite} target="_blank" rel="noopener noreferrer">Watch Now</a>
          </div>
        </div>
      </div>

    </div>

  )
}

export default Summary
